{
  "prompt": "Show me all acquisitions that occurred in January 2001, including acquisition id, acquiring and acquired company names, price, term code, and the source URL"
}

{
    "prompt": "List all acquisitions in £ from 2012, including acquisition ID, price, and the names of both companies.  Include all available metdata"
}

{
    "prompt": "Get all acquisitions where the term code is cash, including the acquisition id, price, companies involved, and the date of the deal.  Include all available metdata"
}

{
    "prompt": "Return 50 acquisitions starting from the 101st acquisition, including company names, price, term code, and acquisition date.  Include all available metdata"
}

{
    "prompt": "List the 20 most expensive acquisitions, including acquisition ID, price, and companies involved, sorted descending by price.  Include all available metdata"
}

{
    "prompt": "List all acquisitions paid in USD with a cash term code from 2000 to 2003, including both companies’ names, prices, and acquisition dates.  Include all available metdata"
}

{
    "prompt": "Show all acquisitions made by the company with ID ‘c:11’, including prices, dates, and acquired company names.  Include all available metdata"
}

{
    "prompt": "Get metadata for all acquisitions in 1966 & 1967, including total count, min/max price, average price, sum of all prices, and earliest/latest acquisition dates."
}

{
    "prompt": "Return the details of acquisition ID 1000, including both company names, price, term code, and acquisition date."
}

{
  "query": "query { acquisitions { rows { id acquisition_id acquiring_object_id acquiring_company { name } acquired_object_id acquired_company { name } price term_code acquired_at source_url source_description created_at updated_at } metadata { totalCount minPrice maxPrice avgPrice sumPrice earliestDate latestDate currencyCounts { currency count } distinctAcquiringCompanies distinctAcquiredCompanies } } }"
}

{
  "query": "query { acquisition(id: 1000) { id acquisition_id acquiring_company { name } acquired_company { name } term_code price_amount price_currency_code acquired_at source_url source_description created_at updated_at price } }"
}

{
  "query": "query { acquisitions(term_code: \"cash\", currency: \"GBP\") { rows { id source_description price term_code acquired_at source_url } metadata { totalCount minPrice maxPrice avgPrice sumPrice } } }"
}

{
  "query": "query { acquisitions(acquired_from: \"2001-01-01\", acquired_to: \"2001-01-31\") { rows { id source_description price term_code acquired_at source_url } metadata { totalCount minPrice maxPrice avgPrice sumPrice } } }"
}

{
  "query": "query { acquisitions(limit: 5, sort_by: price_amount, sort_order: DESC) { rows { id acquisition_id price_amount term_code acquired_at acquiring_company { name } acquired_company { name } } metadata { totalCount minPrice maxPrice avgPrice sumPrice earliestDate latestDate } } }"
}

{
  "query": "query { acquisitions(term_code: \"cash\", limit: 5, sort_by: acquired_at, sort_order: DESC) { rows { id acquisition_id price_amount term_code acquired_at acquiring_company { name } acquired_company { name } } metadata { totalCount minPrice maxPrice avgPrice sumPrice earliestDate latestDate } } }"
}

{
  "query": "query { acquisitions(currency: \"GBP\", limit: 5, sort_by: price_amount, sort_order: DESC) { rows { id acquisition_id price_amount term_code acquired_at acquiring_company { name } acquired_company { name } } metadata { totalCount minPrice maxPrice avgPrice sumPrice earliestDate latestDate } } }"
}

{
  "query": "query { acquisitions(acquiring_object_id: \"c:11\") { rows { id acquisition_id acquiring_object_id acquiring_company { name } acquired_object_id acquired_company { name } price term_code acquired_at source_url source_description created_at updated_at } metadata { totalCount minPrice maxPrice avgPrice sumPrice earliestDate latestDate currencyCounts { currency count } distinctAcquiringCompanies distinctAcquiredCompanies } } }"
}

{
  "query": "query { acquisitions(acquired_object_id: \"c:10\") { rows { id acquisition_id acquiring_object_id acquiring_company { name } acquired_object_id acquired_company { name } price term_code acquired_at source_url source_description created_at updated_at } metadata { totalCount minPrice maxPrice avgPrice sumPrice earliestDate latestDate currencyCounts { currency count } distinctAcquiringCompanies distinctAcquiredCompanies } } }"
}

{
  "query": "query { acquisitions(acquired_from: \"2001-01-01\", acquired_to: \"2002-01-31\" sort_by: price_amount) { rows { id acquired_at source_description price } metadata { totalCount minPrice maxPrice avgPrice sumPrice } } }"
}
